    local speedInput = gg.prompt({"๐ ุงุฏุฎููู ููููููุฉ ุงูุชูุณูุฑูููููุน : {ุงูููููุตููู ุจููู: 15}"}, {}, {"number"})
    if speedInput then
        local targetSpeed = tonumber(speedInput[1])
        if targetSpeed then
            local currentSpeed = gg.getSpeed()
            if currentSpeed then
                local increment
                
                -- ุชุญุฏูุฏ ูููุฉ ุงูุฒูุงุฏุฉ ุจูุงุกู ุนูู ุงูุดุฑูุท
                if targetSpeed <= 30 then
                    increment = 3
                else
                    increment = 6
                end
                
                local delay = 4000  -- ุชุฃุฎูุฑ ุจูู ูู ุฒูุงุฏุฉ ูู ุงูุณุฑุนุฉ ุจุงูููููู ุซุงููุฉ
                
                if targetSpeed > currentSpeed then
                    local tempSpeed = currentSpeed
                    while tempSpeed < targetSpeed do
                        tempSpeed = tempSpeed + increment
                        if tempSpeed > targetSpeed then
                            tempSpeed = targetSpeed
                        end
                        gg.setSpeed(tempSpeed)
                        gg.sleep(delay)
                    end
                end
            end
        end
    end
end

