function Speed() gg.setVisible(false)
    gg.alert(msgSpeed)
    local speedInput = gg.prompt({"📃 ادخــل قـيـمـة التـسـريــــع : {المــوصــي بــه: 15}"}, {}, {"number"})
    if speedInput then
        local targetSpeed = tonumber(speedInput[1])
        if targetSpeed then
            local currentSpeed = gg.getSpeed()
            if currentSpeed then
                local increment
                
                -- تحديد قيمة الزيادة بناءً على الشروط
                if targetSpeed <= 30 then
                    increment = 3
                else
                    increment = 6
                end
                
                local delay = 4000  -- تأخير بين كل زيادة في السرعة بالميللي ثانية
                
                if targetSpeed > currentSpeed then
                    local tempSpeed = currentSpeed
                    while tempSpeed < targetSpeed do
                        tempSpeed = tempSpeed + increment
                        if tempSpeed > targetSpeed then
                            tempSpeed = targetSpeed
                        end
                        gg.setSpeed(tempSpeed)
                        gg.sleep(delay)
                    end
                end
            end
        end
    end
end

